import React , { useState } from "react"

function CharComments ( { currentChar } ) {



    const comments = currentChar.comments.sort((a, b) => parseFloat(b.votes) - parseFloat(a.votes)).map((entry) => {
        let filler = entry
        let [update, setUpdate] = useState(0)
        setUpdate(filler.votes + 1)

        function handleClick(e){
            e.preventDefault()
            filler.votes = update
            console.log(filler)

            fetch(`http://localhost:3000/characters/${currentChar.id}`, {
                method : 'PATCH',
                headers : { "content-type" : "application/json"},
                body : JSON.stringify({...currentChar,
                    [entry.id] : filler
                   })


            }).then(setUpdate(update + 1))

        }

        return (
            <div className="comments">
                <span className="details">
                    <ul>{entry.name}</ul><span className="score" onClick={e => handleClick(e)}>{update}</span>
                    <ul>{entry.content}</ul>

                </span>
            </div>
        )
    })


    return(
        <>
        {comments}
        </>
    )
}

export default CharComments;
