import React , { useState } from "react"
import TeamChars from "./TeamChars";

function Team( { teamChars } ) {

    const [pointAssist, setPointAssist] = useState("A")
    const [midAssist, setMidAssist] = useState("A")
    const [anchorAssist, setAnchorAssist] = useState("A")


    const teamArray = teamChars.map((char) => {
        return <TeamChars key={char.id} char={char}/>
    })

    return (
        <div className="teamCharContainer">
            <span className="teamChars"> <b>Anchor</b>
                {teamArray[2]}
                {teamArray[2]? <span className={anchorAssist}>{anchorAssist}</span>:null}
            </span>
            <span className="teamChars"><b>Mid</b>
                {teamArray[1]}
                {teamArray[1]? <div className={midAssist}>{midAssist}</div>:null}
            </span>
            <span className="teamChars"><b>Point</b>
                {teamArray[0]}
                {teamArray[0]? <div className={pointAssist}>{pointAssist}</div>:null}
            </span>
        </div>
    )
}

export default Team;
