import React from "react"

function CharComments ( { currentChar } ) {

    const comments = currentChar.comments.sort((a, b) => parseFloat(b.votes) - parseFloat(a.votes)).map((entry) => {
        let voted = false
        const filler = entry.votes

        function handleClick(e){
            e.preventDefault()
            const update = ((voted) ? (filler + 1) : (filler-1))

            fetch(`http://localhost:3000/characters/${currentChar.id}/comments`, {
                method : 'PATCH',
                headers : { "content-type" : "application/json"},
                body : JSON.stringify({comments,
                    votes : update
                   })

            }).then(!voted)

        }

        return (
            <div className="details">
                <>
                    <ul>{entry.name}</ul><span className="score" onClick={e => handleClick(e)}>{entry.votes}</span>
                    <ul>{entry.content}</ul>

                </>
            </div>
        )
    })


    return(
        <>
        {comments}
        </>
    )
}

export default CharComments;
