import React , { useState } from "react"
import TeamChars from "./TeamChars";

function Team( { teamChars , setTeamChars} ) {

    let assistArray = ["A", "A", "A"]


    //I know I could break this apart but it works like this and
    const teamNames = teamChars.map((char) => {
        return (
            <td key={char.name}>{char.name}</td>
        )
    })

    const teamImages = teamChars.map((char) => {
        return (
            <td key={char.images.mainImage}><img src={char.images.mainImage} className="teamImages" alt=""/></td>
        )

    })

    const teamAssists = assistArray.map((assist) => {
        let filler = "A"
        function handleClick(e){
            console.log(e.target)
            filler = (e.target.innerText)
        }

        return (
            <td className={filler} onClick={handleClick}>
                <span onClick={handleClick}>A</span>
                <span onClick={handleClick}>B</span>
                <span onClick={handleClick}>C</span>
            </td>
        )
    })

    const teamStrengths = teamChars.map((char) => {
        const filler = char.strengths.keywords.sort().map((word) => {
            return (
                    <ul className="details">+{word}   </ul>
            )
        })
        return (
            <td>{filler}</td>
        )
    })

    const teamWeaknesses = teamChars.map((char)=>{
        const filler = char.weaknesses.keywords.sort().map((word) => {
            return (
                    <ul className="details">-{word}   </ul>

            )
        })
        return (
            <td>{filler}</td>
        )
    })

    const teamRoles = teamChars.map((char) => {
            const prePos = ((char.positions.point[0]==1)?"Point":(char.positions.mid[0]==1?"Mid":"Anchor"))
            return (
                <td>{prePos}</td>
            )
})

    const removeButtons = teamChars.map((char) => {
        function handleRemove(e){
            setTeamChars(teamChars.filter((char)=> char.id != e.target.id))
        }
        return (
            <td><button onClick={handleRemove} id={char.id}>Remove</button></td>
        )
    })




    return (
        <div id="tableHolder">
        <table id="teamCharTable">
            <tbody>
                <tr className="teamCharContainer">
                    <td> </td>
                    <td>Point</td>
                    <td>Mid</td>
                    <td>Anchor</td>
                </tr>
                <tr className="teamCharContainer">
                    <td>Name</td>
                    {teamNames}
                </tr>
                <tr className="teamCharContainer">
                    <td>Image</td>
                    {teamImages}
                </tr>
                <tr className="teamCharContainer">
                    <td>Assist</td>
                    {teamAssists}
                </tr>
                <tr className="teamCharContainer">
                    <td>Strengths</td>
                    {teamStrengths}
                </tr>
                <tr className="teamCharContainer">
                    <td>Weaknesses</td>
                    {teamWeaknesses}
                </tr>
                <tr className="teamCharContainer">
                    <td>Preferred Role</td>
                    {teamRoles}

                </tr>
                <tr className="buttonContainer">
                    <td>Remove</td>
                    {removeButtons}
                </tr>
            </tbody>
        </table>
        <br></br>
        </div>

    )
}

export default Team;
