import React from "react"

function CharComments ( { currentChar } ) {

    const comments = currentChar.comments.sort((a, b) => parseFloat(b.votes) - parseFloat(a.votes)).map((entry) => {
        let filler = entry
        let update = filler.votes + 1
        const allElse = [...currentChar.comments.filter((comment) => {comment.id !== entry.id})]
        console.log(allElse)

        function handleClick(e){
            e.preventDefault()
            filler.votes = update

            console.log(filler)
            console.log(currentChar.comments[entry.id])

            fetch(`http://localhost:3000/characters/${currentChar.id}`, {
                method : 'PATCH',
                headers : { "content-type" : "application/json"},
                body : JSON.stringify({...currentChar,
                    comments : currentChar.comments.splice([entry.id-1], 1, filler)
                   })


            }).then(update + 1)

        }

        return (
            <div className="comments">
                <span className="details">
                    <ul>{entry.name}</ul><span className="score" onClick={e => handleClick(e)}>{entry.votes}</span>
                    <ul>{entry.content}</ul>
                </span>
            </div>
        )
    })


    return(
        <>
        {comments}
        </>
    )
}

export default CharComments;
