import React ,  { useState } from "react"

function CharComments ( { currentChar } ) {

    const comments = currentChar.comments.sort((a, b) => parseFloat(b.votes) - parseFloat(a.votes)).map((entry) => {

        let voted = false

        function handleClick(e){
            e.preventDefault()
            let filler = entry

            (voted) ? (filler.comments[entry.id].votes + 1) : (filler.comments[entry.id].votes - 1)

            fetch(`http://localhost:3000/characters/${currentChar.id}/comments/${entry.id}`, {
                method : 'PATCH',
                headers : { "content-type" : "application/json"},
                body : JSON.stringify({ ...currentChar,
                comments : [...comments[entry.id].votes=filler]})

            }).then(!voted)

        }

        return (
            <div className="details">
                <>
                    <ul>{entry.name}</ul><span className="score" onClick={handleClick}>{entry.votes}</span>
                    <ul>{entry.content}</ul>

                </>
            </div>
        )
    })


    return(
        <>
        {comments}
        </>
    )
}

export default CharComments;
