import React , { useState } from "react"
import TeamChars from "./TeamChars";

function Team( { teamChars , setTeamChars} ) {

    const [pointAssist, setPointAssist] = useState("A")
    const [midAssist, setMidAssist] = useState("A")
    const [anchorAssist, setAnchorAssist] = useState("A")
    const assistArray = [(teamChars[0]?pointAssist:null), (teamChars[1]?midAssist:null), (teamChars[2]?anchorAssist:null)]


    //I know I could break this apart but it works like this and
    const teamNames = teamChars.map((char) => {
        return (
            <td key={char.name}>{char.name}</td>
        )
    })
    const teamImages = teamChars.map((char) => {
        return (
            <td key={char.images.mainImage}><img src={char.images.mainImage} className="teamImages" alt=""/></td>
        )

    })
    const teamAssists = assistArray.map((assist) => {
        return (
            <td className={assist}>{assist}</td>
        )
    })
    const teamStrengths = teamChars.map(() => {
        return <td></td>
    })
    const teamWeaknesses = teamChars.map(()=>{
        return <td></td>
    })
    const removeButtons = teamChars.map((char) => {
        function handleRemove(e){
            setTeamChars(teamChars.filter((char)=> char.id != e.target.id))
        }
        return (
            <td><button onClick={handleRemove} id={char.id}>Remove</button></td>
        )
    })



    return (
        <div id="tableHolder">
        <table id="teamCharTalbe">
            <tbody>
                <tr className="teamCharContainer">
                    <td>Point</td>
                    <td>Mid</td>
                    <td>Anchor</td>
                </tr>
                <tr className="teamCharContainer">
                    {teamNames}
                </tr>
                <tr className="teamCharContainer">
                    {teamImages}
                </tr>
                <tr className="teamCharContainer">
                    {teamAssists}
                </tr>
                <tr>
                    {removeButtons}
                </tr>
            </tbody>
        </table>
        </div>

    )
}

export default Team;
