import React , { useState , useEffect } from "react";
import CommentForm from "./CommentForm"
import CharComments from "./CharComments"
import CharacterMenu from "./CharacterMenu";

function CharPage ( { chara , user , characters } ) {


    const [char, setChar] = useState({})

    useEffect(() => {
        fetch(`http://localhost:3000/characters/${chara.id}`)
        .then(r=> r.json())
        .then(char => setChar({
            ...char,
            //breaks the text into arrays
            strengths_keywords: ("\" " + char.strengths_keywords + " \"").replaceAll("[", "").replaceAll("]", "").replaceAll(",", "").split("\" \"").slice(1, -1),
            weaknesses_keywords: ("\" " + char.weaknesses_keywords + " \"").replaceAll("[", "").replaceAll("]", "").replaceAll(",", "").split("\" \"").slice(1, -1),
            strengths_text: ("\" " + char.strengths_text + " \"").replaceAll("[", "").replaceAll("]", "").replaceAll(",", "").split("\" \"").slice(1, -1),
            weaknesses_text: ("\" " + char.weaknesses_text + " \"").replaceAll("[", "").replaceAll("]", "").replaceAll(",", "").split("\" \"").slice(1, -1)}))
    }, [])



    const charStrengths = char.strengths_keywords.map((word) => {
            return (
                    <ul className="details">+{word}</ul>
            )
        })

    const charWeaknesses = char.weaknesses_keywords.map((word) => {
            return (
                    <ul className="details">-{word}</ul>
            )
        })

    const charComments = char.comments.map((comment) => {


        return (
            <>
                <CharComments comment={comment}></CharComments>
            </>
        )

    })

    return(
        <>
            <div className="tableHolder">
                <table className="mainCharTable">
                    <tbody>
                        <tr className="teamCharContainer">
                            {char.name}
                        </tr>
                        <tr  className="teamCharContainer">
                            <img src={char.teamImage} alt="" className="teamImages"/>
                        </tr>
                        <tr  className="teamCharContainer">
                            <span>{charStrengths}</span>

                        </tr>
                        <tr  className="teamCharContainer">
                        <span>{charWeaknesses}</span>
                        </tr>
                        <tr  className="teamCharContainer">
                            <td>Comments
                            {charComments}
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
            <br></br>
            {user?<CommentForm char={char} user={user}/>:null}
            <br></br>
            <CharacterMenu characters={characters} commentPage={true}/>
        </>
    )


}


export default CharPage
