function CharacterButton( { image , teamChars , setTeamChars , char } ) {
    function handleClick(e) {
        e.preventDefault()
        let newArray = teamChars
        teamChars[0] ? (teamChars[1] ? (teamChars[2]? null: newArray[2]=char): newArray[1]=char ) :(newArray[0] = char)
        setTeamChars([...newArray])
    }

    return <img src={image} alt="" onClick={handleClick}/>
}

export default CharacterButton;
