import React, { useState, useEffect } from 'react';
import './App.css';
import Team from './components/Team';
import CharacterMenu from './components/CharacterMenu';
import NavBar from './components/NavBar';
import Comments from './components/Comments';

function App() {

  const [characters, setCharacters] = useState([])
  const [teamChars, setTeamChars] = useState([])
  const [recentChar, setRecentChar] = useState([])
  const [teamBuilder, setTeamBuilder] = useState(true)


  useEffect(() => {
    fetch("http://localhost:3000/characters")
    .then(r => r.json())
    .then(res => {
      setCharacters(res)
    })

  }, [])

  return (
    <>
      <a href='https://www.dustloop.com/w/Dragon_Ball_FighterZ'>Credit to Dustloop</a>
      <NavBar/>
      {teamBuilder?<Team teamChars={teamChars} setTeamChars={setTeamChars}/>:<Comments/>}
      <CharacterMenu characters={characters} teamChars={teamChars} setTeamChars={setTeamChars}/>
    </>
  );
}

export default App;
