import React from "react"

function CharacterButton( { image , teamChars , setTeamChars , char } ) {
    function handleClick(e) {
        e.preventDefault()
        let newArray
        if(teamChars[0]===undefined){
            newArray = [char, ...teamChars.slice(1)]

            setTeamChars(newArray)
        }
        else if (teamChars[1]===undefined){
            newArray = [teamChars[0], char, teamChars[2]]
            setTeamChars(newArray)
        }
        else if(teamChars[2]===undefined){
            newArray = [teamChars[0], teamChars[1], char]
            setTeamChars(newArray)
        }

        console.log(newArray)
    }

    return <img src={image} alt="" onClick={handleClick}/>
}

export default CharacterButton;
