import React , { useState } from "react"
import TeamChars from "./TeamChars";

function Team( { teamChars , setTeamChars} ) {

    const [pointAssist, setPointAssist] = useState("A")
    const [midAssist, setMidAssist] = useState("A")
    const [anchorAssist, setAnchorAssist] = useState("A")
    const assistArray = [pointAssist, midAssist, anchorAssist]

    const teamNames = teamChars.map((char) => {
        return <td>{char.name}</td>
    })
    const teamImages = teamChars.map((char) => {
        return <td><img src={char.images.mainImage} className="teamImages" alt=""/></td>
    })
    const teamAssists = assistArray.map((assist) => {
        return <td className={assist}>{assist}</td>
    })
    const teamStrengths = teamChars.map(() => {
        return <td></td>
    })
    const teamWeaknesses = teamChars.map(()=>{
        return <td></td>
    })
    const removeButtons = teamChars.map((char) => {
        function handleRemove(e){
            console.log(e.target)
        }
        return (<td><button onClick={handleRemove} id={char.id}>Remove</button></td>)
    })



    return (
        <div id="tableHolder">
        <table>
            <tbody>
                <tr className="teamCharContainer">
                    <td>Point</td>
                    <td>Mid</td>
                    <td>Anchor</td>
                </tr>
                <tr className="teamCharContainer">
                    {teamNames}
                </tr>
                <tr className="teamCharContainer">
                    {teamImages}
                </tr>
                <tr className="teamCharContainer">
                    {teamAssists}
                </tr>
                <tr>
                    {removeButtons}
                </tr>
            </tbody>
        </table>
        </div>

    )
}

export default Team;
